<?php
include_once('../config.php');
include_once('../php/functions.php');
include_once('../class/directorio.php');
$idOficina = isset($_POST['idOficina']) ? $_POST['idOficina'] : '1';//echo "-->" . $idOficina;
$directorio = new Directorio();
$details_dir = $directorio->getAllEmpleadosByIdOficina($idOficina);
//echo nl2br(print_r($details_dir, true));$directorio_html = "";$nCountReg = count($details_dir);$i = 0;foreach ($details_dir as $clave => $valor ) {	//$valor['nombre'] = limpiarString($valor['nombre']);	$sNombre = "<strong>{$valor['nombre']}</strong>";	$sNomPuesto = "<strong>{$valor['nompuesto']}</strong><br>";$sTelefono = "<i class=\"fa fa-fw fa-phone\"></i> Tel: {$valor['telefono']}<br>";$sMovil = "Nextel: {$valor['movil']}<br>";$sCorreoe = "<i class=\"fa fa-fw fa-envelope\"></i>&nbsp;<a href=\"mailto:{$valor['correoe']}\">{$valor['correoe']}</a>";$directorio_ini = <<<EOPAGE	                        <div class="row invoice-info" style="margin-bottom: 0px;">                            <div class="col-sm-6">                              {$sNombre}                              <address>                                {$sNomPuesto}                                {$sTelefono}                                Ext: {$valor['extension']}<br>                                {$sMovil}                                {$sCorreoe}                              </address>                            </div>	EOPAGE;	$directorio_fin = <<<EOPAGE                                                    <div class="col-sm-6">                              {$sNombre}                              <address>                                {$sNomPuesto}                                {$sTelefono}                                Ext: {$valor['extension']}<br>                                {$sMovil}                                {$sCorreoe}                              </address>                            </div>                        </div>	EOPAGE;		if ($i % 2) {				$directorio_html .= $directorio_fin;							} else {			$directorio_html .= $directorio_ini;	}		$i++;	}if (!($i-1 % 2)) {	$directorio_fin_aux = <<<EOPAGE	                            <div class="col-sm-6">                              &nbsp;                            </div>                        </div>	EOPAGE;$directorio_html .= $directorio_fin_aux;}echo $directorio_html;
?>